var env = process.env;
var request = require('request');

var data = {
	uri: 'https://api.skybiometry.com/fc/faces/detect',
	method: 'PUT',
	formData: {
		api_key: env.SKYBIOMETRY_API_KEY,
		api_secret: env.SKYBIOMETRY_API_SECRET,
		urls: ['http://groovekorea.com/wp-content/uploads/2015/05/zizek2.jpg'],
		detect_all_feature_points: 'true'
	}
};

function detectFace(imgUrl, callback) {
	data.formData.urls[0] = imgUrl;
	request(data, callback);
}

module.exports = {
	detectFace: detectFace
}